mapper.delete:
DELETE FROM favorites
WHERE user_id = #{userId}
AND attraction_id = #{attractionId}

mapper.select:
SELECT COUNT(*) > 0
FROM favorites
WHERE user_id = #{userId} AND attraction_id = #{attractionId}

SELECT a.no, a.content_id AS contentId, a.title, a.latitude, a.longitude, a.addr1 AS address, a.first_image1 AS firstImage1, a.hit, a.views FROM favorites f JOIN attractions a ON f.attraction_id = a.no WHERE f.user_id = #{userId}
AND a.title LIKE CONCAT('%', #{word}, '%')
ORDER BY a.title ASC LIMIT #{offset}, #{size}
