mapper.select:
SELECT notice_id AS noticeId, title, created_at AS createdAt FROM notices
AND ( title LIKE CONCAT('%', #{keyword}, '%') OR content LIKE CONCAT('%', #{keyword}, '%') )
ORDER BY created_at DESC LIMIT #{size} OFFSET #{offset}

mapper.update:
UPDATE notices
SET
    title = #{notice.title},
    content = #{notice.content},
    image_url = #{notice.imageUrl}
WHERE notice_id = #{noticeId}
